(function(){if(typeof crystal=="undefined"){return}var LVIEW_BUFFER_LENGTH=50;eval(crystal.output("crystal"));evt.ready(function(){var locs=map(dom.getElementsByClassName("w-l-qq-com",["span"]),function(el){return el.id});while(locs.length){request(locs.splice(0,LVIEW_BUFFER_LENGTH))}});var request=function(locs){importScript("http://w.l.qq.com/lview?c=www&loc="+locs.join(",")+"&type=text&callback=appTargetText")};var display_index=getPlayIndex();var render=function(span,text,url,color){if(!span){return}if(!text){text="\u5728\u7ebf\u7cbe\u5f69\uff0c\u751f\u6d3b\u66f4\u7cbe\u5f69";url="http://www.qq.com"}var a=document.createElement("a");a.className=span.className;a.target="_blank";a.href=url;if(color){a.style.color=color}a.appendChild(document.createTextNode(text));span.parentNode.replaceChild(a,span)};window.appTargetText=function(o){each(o,function(displays,el_id){var display=displays[display_index%displays.length];if(display.need_ping){ping.append({loc:el_id,cid:display.cid,oid:display.oid})}render.apply(window,[document.getElementById(el_id)].concat(display.fodder))})}})();
